import React, {useState, useEffect} from "react";
import Nav from './Nav';
import NewRecipeForm from './NewRecipeForm';
import RecipeList from './RecipeList';
import Home from './Home';
import {Route, Switch} from 'react-router-dom';


function App(){
    const [recipes, setRecipes] = useState([]);
    const [searchInput, setSearchInput] = useState("");

useEffect(() => {
    fetch('http://localhost:3001/recipes')
    .then(res => res.json())
    .then(data => setRecipes(data))
}, [])

function onNewFormSubmit(newRecipe){
    setRecipes([...recipes, newRecipe])
}

    const recipesToDisplay = recipes.filter(recipe => recipe.name.toLowerCase().includes(searchInput.toLowerCase()));

    return (
        <div className="app-container">
            <Nav />
            <Switch>
                <Route exact path="/">
                    <Home />
                </Route>
                <Route path="/recipes"> 
                    <RecipeList setSearchInput={setSearchInput} searchInput={searchInput} recipes={recipesToDisplay}/>
                </Route>
                <Route path="/recipeform">
                    <NewRecipeForm onFormSubmit={onNewFormSubmit}/>
                </Route>
            </Switch>
        </div>
    )
}

export default App;
