import React, {useState} from "react";

function NewRecipeForm({onFormSubmit}){
    const [category, setCategory] = useState("Choose Category");
    const [name, setName] = useState("");
    const [description, setDescription] = useState("");
    const [instructions, setInstructions] = useState("");
    const [ingredients, setIngredients] = useState("");
    const [cook_time, setCooktime] = useState("");
    const [image_url, setImage] = useState("");

    function handleSubmit(event){
        const newRecipe ={
            name,
            description,
            instructions,
            ingredients,
            cook_time,
            category,
            image_url
        }
        onFormSubmit(newRecipe)
    }


    return (
        <div className="recipe-form-container">
            <label>
                Post Your Spectactular Recipe!
            </label>
            <input placeholder="Enter recipe name..." className="input-field" type="text"/>
            <input placeholder="Enter recipe description..." className="input-field" type="text"/>
            <input placeholder="Enter recipe instructions..." className="input-field" type="text"/>
            <input placeholder="Enter recipe ingredients..." className="input-field" type="text"/>
            <input placeholder="Enter recipe cook time..." className="input-field" type="text"/>
            <input placeholder="Enter recipe category..." className="input-field" type="text"/>
            <select value={category} className="input-field">
                <option>Choose Category</option>
                <option>Italian</option>
                <option>Asian</option>
                <option>Mexican</option>
                <option>Dessert</option>
                <option>Vegan</option>
            </select>
            <input placeholder="Enter recipe image..." className="input-field" type="text"/>
            <button className="input-field-button">Add New Recipe</button>
        </div>
    )
}

export default NewRecipeForm;
